import React from 'react';
import {Container, Row, Button} from 'reactstrap';
import { NavLink,Link } from 'react-router-dom';
import logo from '../../assets/images/logo.png'
import "./header.css";

const nav__links=[
  {
    path:'/home',
    display:'Home'
  },
  {
    path:'/sites',
    display:'Sites'
  },
  {
    path:'/sites',
    display:'Sites'
  }
]

const Header = () => {
  return(
     <header className='w-full h-14 bg-slate-300 '>
    <Container className='justify-between items-center'>
      <Row >
        <div className='flex items-center justify-between  '>
        <div className=" flex items-center justify-between">
          <img src={logo} alt='logoimg' className='w-40'/>
        </div>
        <div className='navigation py-2'>
          <ul class='menu flex flex-row items-center justify-between gap-20'>
            {
              nav__links.map((item,index)=>(
                <li className='nav__item ' key={index}>
                  <NavLink to={item.path} className='no-underline text-zinc-950 '>{item.display}</NavLink>
                </li>
              ))}

          </ul>

        </div>
        <div className='flex  items-center justify-between gap-4 '>
          <div className='flex flex-row items-center justify-between gap-1 '>
          <Button className='text-zinc-950 bg-transparent hover:bg-blue-500 text-font-semibold hover:text-white py-1 px-4 border border-blue-500 hover:border-transparent rounded '>
            <Link to='/login' className='no-underline'>Login</Link>
          </Button>
          <Button className='bg-transparent hover:bg-blue-500 text-blue-700 font-semibold hover:text-white py-1 px-4 border border-blue-500 hover:border-transparent rounded'> 
            <Link to='/register' className='no-underline'>Register</Link>
          </Button>
          


          </div>
          <span>
          <i class="ri-menu-line"></i>
          </span>
        </div>

        </div>
      </Row>
    </Container>
  </header>
  )
}

export default Header
